mapper.insert:
INSERT INTO t_dm_user
SET idm = #{idm}
, iuser = #{iuser}


    SELECT IFNULL(MAX(seq), 0) + 1
    FROM t_dm_msg
    WHERE idm = #{idm}

INSERT INTO t_dm_msg
SET idm = #{idm}
, seq = #{seq}
, iuser = #{loginedIuser}
, msg = #{msg}

mapper.select:
UPDATE t_dm
SET last_msg = #{msg}
WHERE idm = #{idm}
