mapper.insert:
INSERT INTO t_user
SET uid = #{uid}
, upw = #{upw}
, nm = #{nm}
, pic = #{pic}

mapper.select:
SELECT iuser, nm, pic, upw
FROM t_user
WHERE uid = #{uid}

mapper.update:
UPDATE t_user
SET pic = #{pic}
WHERE iuser = #{iuser}

mapper.insert:
